from Crypto.Util.number import *
import random

def prime_gen():
	p = getPrime(512)
	q = 0
	while not isPrime(q):
		temp = p
		q = p + random.randint(1,1000)
		p = temp
	return p, q



FLAG = bytes_to_long(b"LKSJAKTIM{FAKE_FLAG_DONT_SUBMIT}")
p, q = prime_gen()
e = 65537
n = p * q
print("n = ", str(n))
print("c = ", str(pow(FLAG,e,n)))

